mapper.select:
select lecture_no, board_cd, no, seq_no, auth_nm, title, read_cnt, update_id, update_date from board where 1=1 and lecture_no = ${lecture_no} and seq_no = 0
and board_cd = #{board_cd}
order by update_date desc

mapper.update:
update
	board
set
	content = #{content},
	update_id = #{update_id},
	update_date = now()
where 1=1
	and lecture_no = #{lecture_no}
	and board_cd = #{board_cd}
	and no = #{no}
	and seq_no = #{seq_no}

mapper.delete:
delete from
	board
where 1=1
	and lecture_no = #{lecture_no}
	and board_cd = #{board_cd}
	and no = #{no}

delete from
	board
where 1=1
	and lecture_no = #{lecture_no}
	and board_cd = #{board_cd}
	and no = #{no}
	and seq_no = #{seq_no}
