mapper.select:
select lecture_no, course_no, question_no, save_file_nm, open from course where 1=1 and lecture_no = #{lecture_no}
and course_no = #{course_no}
and course_no = ( select max(pass_course_no) + 1 from enroll where 1=1 and lecture_no = #{lecture_no} and user_id = #{user_id} )

select lecture_no, course_no, question_no, question, file_nm, save_file_nm, open from course where 1=1 and lecture_no = #{lecture_no}
and course_no = #{course_no}
and course_no = (select max(course_no) from course where lecture_no = #{lecture_no})

mapper.update:
update course set question = #{question},
file_nm = #{file_nm}, save_file_nm = #{save_file_nm},
open = #{open}, update_id = #{update_id}, update_date = now() where 1=1 and lecture_no = #{lecture_no} and course_no = #{course_no} and question_no = #{question_no}
